import React, { useEffect, useState } from "react";

function App() {
  // State
  const [users, setUsers] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [editingUser, setEditingUser] = useState(null);

  // L·∫•y danh s√°ch user
  const fetchUsers = async () => {
    const res = await fetch("http://localhost:5000/api/users");
    const data = await res.json();
    setUsers(data);
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // Th√™m ho·∫∑c c·∫≠p nh·∫≠t user
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (editingUser) {
      await fetch(`http://localhost:5000/api/users/${editingUser._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email }),
      });
      setEditingUser(null);
    } else {
      await fetch("http://localhost:5000/api/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email }),
      });
    }

    setName("");
    setEmail("");
    fetchUsers();
  };

  // X√≥a user
  const handleDelete = async (id) => {
    await fetch(`http://localhost:5000/api/users/${id}`, {
      method: "DELETE",
    });
    fetchUsers();
  };

  // S·ª≠a user
  const handleEdit = (user) => {
    setEditingUser(user);
    setName(user.name);
    setEmail(user.email);
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Qu·∫£n l√Ω ng∆∞·ªùi d√πng</h1>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="T√™n"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <button type="submit">{editingUser ? "C·∫≠p nh·∫≠t" : "Th√™m"}</button>
      </form>

      <h2>Danh s√°ch ng∆∞·ªùi d√πng</h2>
      <ul>
        {users.map((u) => (
          <li key={u._id}>
            {u.name} - {u.email}{" "}
            <button onClick={() => handleEdit(u)}>‚úèÔ∏è S·ª≠a</button>{" "}
            <button onClick={() => handleDelete(u._id)}>üóëÔ∏è X√≥a</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
